    "id": "3704315386891280390",
    "list": {
      "isOrdered": true,
      "item": [{
        "text": "Navigate to Account > Payment information.  carta/public/11/images/3087424856685893406_gNbvP8MvHgxAq1S3b2UU5rM7ZznVoNjiZibi5cUEE3yQHi_dL0JuvuRcpAr1nJt9qOhumnGuHIE7W7fJoNzGjBPqD0NvAboIpWmYKF-_qLRja4SJIM-Cu83C8wO_xzyCl1-hjVXi",
